class MarkdownFormatter { static String format(String text) { String result = text; while (result.contains("**")) { int startIndex = result.indexOf("**"); if (startIndex == -1) break; int endIndex = result.indexOf("**", startIndex + 2); if (endIndex == -1) break; String boldText = result.substring(startIndex + 2, endIndex); result = result.substring(0, startIndex) + boldText + result.substring(endIndex + 2); } while (result.contains("*")) { int startIndex = result.indexOf("*"); if (startIndex == -1) break; int endIndex = result.indexOf("*", startIndex + 1); if (endIndex == -1) break; String italicText = result.substring(startIndex + 1, endIndex); result = result.substring(0, startIndex) + italicText + result.substring(endIndex + 1); } List<String> lines = result.split("\n"); for (int i = 0; i < lines.length; i++) { if (lines[i].trim().startsWith("* ")) { lines[i] = lines[i].replaceFirst("* ", " "); } } return lines.join("\n"); } }
